import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import RazorpayPayment from '../components/RazorpayPayment';

const PaymentPage = () => {
  const navigate = useNavigate();
  const { state } = useLocation();
  const bookingData = state?.bookingData;

  const onSuccess = (data) => {
    alert('Payment successful!');
    navigate('/customer/bookings', { state: { message: 'Payment completed', paymentData: data } });
  };
  const onFailure = (msg) => alert(`Payment failed: ${msg}`);

  if (!bookingData) return <div className="max-w-md mx-auto mt-8 p-6 bg-red-50 rounded">No booking data.</div>;

  return (
    <div className="max-w-2xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6">Complete Payment</h1>
      <div className="bg-white rounded shadow p-6 mb-6">
        <div className="flex justify-between"><span>Service</span><span>{bookingData.serviceName}</span></div>
        <div className="flex justify-between"><span>Date & Time</span><span>{bookingData.date} {bookingData.time}</span></div>
        <div className="flex justify-between"><span>Address</span><span>{bookingData.address}</span></div>
        <hr className="my-3" />
        <div className="flex justify-between font-semibold text-lg"><span>Total</span><span>â‚¹{bookingData.amount}</span></div>
      </div>
      <div className="bg-white rounded shadow p-6">
        <RazorpayPayment amount={bookingData.amount} bookingData={bookingData} onSuccess={onSuccess} onFailure={onFailure} />
      </div>
    </div>
  );
};

export default PaymentPage;
