import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

const PaymentPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const bookingData = location.state?.bookingData;

  const onPay = () => {
    // Integrate Razorpay component later. For now, mock success.
    alert('Payment successful (mock).');
    navigate('/customer/bookings', { state: { message: 'Payment completed' } });
  };

  if (!bookingData) {
    return <div className="max-w-md mx-auto mt-8 p-6 bg-red-50 rounded">No booking data found.</div>;
  }

  return (
    <div className="max-w-xl mx-auto p-6 bg-white rounded-lg shadow mt-8">
      <h1 className="text-2xl font-bold mb-4">Complete Payment</h1>
      <div className="space-y-2 mb-6">
        <div className="flex justify-between"><span>Service</span><span className="font-medium">{bookingData.serviceName}</span></div>
        <div className="flex justify-between"><span>Date & Time</span><span>{bookingData.date} {bookingData.time}</span></div>
        <div className="flex justify-between"><span>Address</span><span>{bookingData.address}</span></div>
        <hr />
        <div className="flex justify-between text-lg font-semibold"><span>Total</span><span>₹{bookingData.amount}</span></div>
      </div>
      <button className="btn-primary w-full py-3" onClick={onPay}>Pay ₹{bookingData.amount}</button>
    </div>
  );
};

export default PaymentPage;
