import React from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import RazorpayPayment from '../components/RazorpayPayment';

const PaymentPage = () => {
    const navigate = useNavigate();
    const location = useLocation();
    const bookingData = location.state?.bookingData;

    const handlePaymentSuccess = (paymentData) => {
        alert('Payment successful!');
        navigate('/customer/bookings', {
            state: { 
                message: 'Booking confirmed and payment completed!',
                paymentData 
            }
        });
    };

    const handlePaymentFailure = (error) => {
        alert(`Payment failed: ${error}`);
    };

    if (!bookingData) {
        return (
            <div className="max-w-md mx-auto mt-8 p-6 bg-red-50 rounded-lg">
                <p className="text-red-600">No booking data found. Please go back and try again.</p>
            </div>
        );
    }

    return (
        <div className="max-w-2xl mx-auto px-4 py-8">
            <h1 className="text-3xl font-bold text-gray-900 mb-8">Complete Payment</h1>

            {/* Order Summary */}
            <div className="bg-white rounded-lg shadow p-6 mb-6">
                <h2 className="text-xl font-semibold mb-4">Order Summary</h2>
                <div className="space-y-3">
                    <div className="flex justify-between">
                        <span>Service:</span>
                        <span className="font-medium">{bookingData.serviceName}</span>
                    </div>
                    <div className="flex justify-between">
                        <span>Date & Time:</span>
                        <span>{bookingData.date} at {bookingData.time}</span>
                    </div>
                    <div className="flex justify-between">
                        <span>Address:</span>
                        <span>{bookingData.address}</span>
                    </div>
                    <hr />
                    <div className="flex justify-between text-lg font-semibold">
                        <span>Total Amount:</span>
                        <span>â‚¹{bookingData.amount}</span>
                    </div>
                </div>
            </div>

            {/* Payment Section */}
            <div className="bg-white rounded-lg shadow p-6">
                <h2 className="text-xl font-semibold mb-4">Payment</h2>
                <RazorpayPayment
                    amount={bookingData.amount}
                    onSuccess={handlePaymentSuccess}
                    onFailure={handlePaymentFailure}
                    bookingData={bookingData}
                />
            </div>
        </div>
    );
};

export default PaymentPage;
